import React, { useState, useEffect } from 'react';
import axios from 'axios';

const OfferList = () => {
  const [filteredOffers, setFilteredOffers] = useState({});

  useEffect(() => {
    // Fetch the filtered offers from the API here or set it in the state
    // For example:
    axios.get('/api/offers').then((response) => {
      setFilteredOffers(response.data);
    });
  }, []);

  return (
    <div>
      <h2>Offer List</h2>
      <ul>
        {Object.values(filteredOffers).map((offer) => {
          // Add this check to see the offer object before rendering
          console.log(offer);

          // Make sure the 'Offer_id', 'title', and 'cost' properties exist in the offer object
          // Replace 'Offer_id' with the actual property name used in your data
          if (!offer || !offer.Offer_id || !offer.title || !offer.cost) {
            return null; // Skip rendering if any of the required properties are missing
          }

          // Assuming 'Offer_id' is a valid property, you can use it as a key
          return (
            <li key={offer.Offer_id}>
              <a href={`/offer/${offer.Offer_id}`}>{offer.title}</a>
              <span>{offer.cost === 0 ? 'Free' : `$${offer.cost}`}</span>
            </li>
          );
        })}
      </ul>
    </div>
  );
};

export default OfferList;
