import React, { useState, useEffect } from 'react';
import axios from 'axios';

const OfferList = () => {
  const [offers, setOffers] = useState([]);
  const [filteredOffers, setFilteredOffers] = useState([]);
  const [filter, setFilter] = useState('all');
  const [sort, setSort] = useState('asc');

  useEffect(() => {
    axios.get('/api/offers')
      .then((response) => {
        console.log('API response:', response.data); // Check the API response in the console
        setOffers(response.data);
        setFilteredOffers(response.data);
      })
      .catch((error) => {
        console.error('Error fetching offers:', error);
      });
  }, []);
  useEffect(() => {
    const offersArray = Object.values(offers);
    let filteredList = offersArray;

    if (filter === 'free') {
      filteredList = offersArray.filter((offer) => offer.cost === 0);
    } else if (filter === 'paid') {
      filteredList = offersArray.filter((offer) => offer.cost > 0);
    }

    if (sort === 'asc') {
      filteredList.sort((a, b) => a.cost - b.cost);
    } else if (sort === 'desc') {
      filteredList.sort((a, b) => b.cost - a.cost);
    }

    const filteredOffersObject = {};
    filteredList.forEach((offer) => {
      filteredOffersObject[offer.Offer_id] = offer;
    });

    setFilteredOffers(filteredOffersObject);
  }, [offers, filter, sort]);

  return (
    <div>
      <h2>Offer List</h2>
      <div>
        <label>Filter:</label>
        <select value={filter} onChange={(e) => setFilter(e.target.value)}>
          <option value="all">All</option>
          <option value="free">Free</option>
          <option value="paid">Paid</option>
        </select>
        <label>Sort:</label>
        <select value={sort} onChange={(e) => setSort(e.target.value)}>
          <option value="asc">Low to High</option>
          <option value="desc">High to Low</option>
        </select>
      </div>
      <ul>
        {Object.values(filteredOffers).map((offer) => (
          <li key={offer.Offer_id}>
            <a href={`/offer/${offer.Offer_id}`}>{offer.title}</a>
            <span>{offer.cost === 0 ? 'Free' : `$${offer.cost}`}</span>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default OfferList;
