import React, {useState} from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';




function Login() {
    const [email, setEmail] = useState('');
    const navigate = useNavigate();

    const handleLogin = () => {
        axios
          .post('/api/login', { email })
          .then((response) => {
            console.log('Login successful:', response.data);
            navigate('/Welcome');
          })
          .catch((error) => {
            console.error('Login failed:', error);

          });
      };
      const wsProtocol = window.location.protocol === 'https:' ? 'wss' : 'ws';
      const wsUrl = `${wsProtocol}://localhost:3000/ws`;

      // WebSocket connection state
      const [socket, setSocket] = useState(null);

      // Function to handle WebSocket connection
      const handleWebSocketConnection = () => {
        const newSocket = new WebSocket(wsUrl);

        newSocket.onopen = () => {
          console.log('WebSocket connection opened.');
          // Additional logic when WebSocket connection is opened, if needed
        };

        newSocket.onmessage = (event) => {
          console.log('Received:', event.data);
          // Additional logic to handle incoming WebSocket messages, if needed
        };

        newSocket.onclose = () => {
          console.log('WebSocket connection closed.');
          // Additional logic when WebSocket connection is closed, if needed
        };

        setSocket(newSocket); // Save the WebSocket instance to state
      };

      // Call the function to establish WebSocket connection on component mount
      React.useEffect(() => {
        handleWebSocketConnection();

        // Clean up the WebSocket connection when the component is unmounted
        return () => {
          if (socket) {
            socket.close();
          }
        };
      }, [handleWebSocketConnection, socket]);


      return (
    <>

    <div className='login-container'>
        <div className='title'>
            <h3>(Airways Fast Delivery)</h3>
        </div>

        <div className='fieldset'>
            <form>
                <fieldset>
                    <legend>Log in</legend>
                    <input type='email' value={email} onChange={(event) => setEmail(event.target.value)} placeholder='E-mail Address'></input>
                    <button onClick={handleLogin}>Log in</button>
                </fieldset>
                <p>if you dont have account already, then click on the link below</p>
                <p> Click on the link to <strong> <Link to = './createUser'>Create user</Link></strong></p>
            </form>

         </div>

    </div>

       </>
  )
}

export default Login
