import React, { useState, useEffect } from 'react';
import axios from 'axios';

const OfferList = () => {
  const [offers, setOffers] = useState([]);
  const [filteredOffers, setFilteredOffers] = useState([]);
  const [filter, setFilter] = useState('all'); // Options: 'all', 'free', 'paid'
  const [sort, setSort] = useState('asc'); // Options: 'asc', 'desc'
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch offers data from the API endpoint
    axios.get('/api/offers')
      .then((response) => {
        setOffers(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching offers:', error);
        setLoading(false);
      });
  }, []);

  useEffect(() => {
    // Apply filters and sorting when 'offers' data or filter/sort state changes
    let filteredList = offers;

    if (filter === 'free') {
      filteredList = offers.filter((offer) => offer.cost === 0);
    } else if (filter === 'paid') {
      filteredList = offers.filter((offer) => offer.cost > 0);
    }

    if (sort === 'asc') {
      filteredList.sort((a, b) => a.cost - b.cost);
    } else if (sort === 'desc') {
      filteredList.sort((a, b) => b.cost - a.cost);
    }

    setFilteredOffers(filteredList);
  }, [offers, filter, sort]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!Array.isArray(offers) || offers.length === 0) {
    return <div>No offers available.</div>;
  }

  return (
    <div>
      <h2>Offer List</h2>
      <div>
        <label>Filter:</label>
        <select value={filter} onChange={(e) => setFilter(e.target.value)}>
          <option value="all">All</option>
          <option value="free">Free</option>
          <option value="paid">Paid</option>
        </select>
        <label>Sort:</label>
        <select value={sort} onChange={(e) => setSort(e.target.value)}>
          <option value="asc">Low to High</option>
          <option value="desc">High to Low</option>
        </select>
      </div>
      <ul>
        {filteredOffers.map((offer) => (
          <li key={offer.OID}>
            <a href={`/offers/${offer.OID}`}>{offer.title}</a>
            <span>{offer.cost === 0 ? 'Free' : `$${offer.cost}`}</span>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default OfferList;
